import React, { useState } from "react";
import api from '../api';
import Step5Confirmation from "../components/Step5Confirmation";
import Stepper from "../components/Stepper";

import './BookSlot.css';

const BookSlot = () => {
  const [confirmedBooking, setConfirmedBooking] = useState(null);
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    service: "",
    fullName: "",
    email: "",
    phone: "",
    resumeLink: "",
    targetRole: "",
    notes: "",
    date: "",
    time: "",
    agree: false,
  });

  const handleNext = () => setStep(prev => prev + 1);
  const handleBack = () => setStep(prev => prev - 1);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handlePayment = async () => {
    try {
      const { data } = await api.post("/payment/create-order", {
        amount: 499,
      });

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: data.amount,
        currency: "INR",
        name: "Apne Wale Coders",
        description: "Slot Booking Payment",
        order_id: data.id,
        handler: async function (response) {
          try {
            await api.post("/payment/verify-payment", {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
            });

            const saveRes = await api.post("/booking/confirm", {
              bookingData: formData,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_order_id: response.razorpay_order_id,
              razorpay_signature: response.razorpay_signature,
            });

            setConfirmedBooking(saveRes.data.booking);
            setStep(6);

          } catch (err) {
            console.error("Payment verification or save failed:", err);
            alert("Something went wrong during payment confirmation.");
          }
        },
        prefill: {
          name: formData.fullName,
          email: formData.email,
          contact: formData.phone,
        },
        theme: {
          color: "#EF4444",
        },
      };

      const razor = new window.Razorpay(options);
      razor.open();
    } catch (error) {
      console.error("Razorpay error:", error);
      alert("Payment Failed");
    }
  };

  return (
    <div className="bookslot-container">
      <Stepper currentStep={step} className="bookslot-stepper" />

      {step === 1 && (
        <div>
          <label className="bookslot-label" htmlFor="service">Desired Service</label>
          <input
            id="service"
            type="text"
            name="service"
            value={formData.service}
            onChange={handleChange}
            placeholder="e.g., Resume Review, Interview Prep"
            className="bookslot-input"
          />
          <div className="bookslot-btn-group" style={{ justifyContent: 'flex-end' }}>
            <button
              onClick={handleNext}
              disabled={!formData.service.trim()}
              className="bookslot-btn bookslot-btn-primary"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div>
          <label className="bookslot-label" htmlFor="fullName">Full Name</label>
          <input
            id="fullName"
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className="bookslot-input"
          />

          <label className="bookslot-label" htmlFor="email">Email</label>
          <input
            id="email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="bookslot-input"
          />

          <label className="bookslot-label" htmlFor="phone">Phone</label>
          <input
            id="phone"
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="bookslot-input"
          />

          <label className="bookslot-label" htmlFor="resumeLink">Resume Link ( if any )</label>
          <textarea
            id="resumeLink"
            type="url"
            name="resumeLink"
            value={formData.resumeLink}
            onChange={handleChange}
            className="bookslot-input"
            placeholder="Google Drive or Dropbox link"
          />

          <label className="bookslot-label" htmlFor="targetRole">Target Role</label>
          <input
            id="targetRole"
            type="text"
            name="targetRole"
            value={formData.targetRole}
            onChange={handleChange}
            className="bookslot-input"
          />

          <label className="bookslot-label" htmlFor="notes">Notes (optional)</label>
          <textarea
            id="notes"
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="bookslot-textarea"
            placeholder="Additional info or questions..."
          />

          <div className="bookslot-btn-group">
            <button
              onClick={handleBack}
              className="bookslot-btn bookslot-btn-secondary"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              disabled={
                !formData.fullName ||
                !formData.email ||
                !formData.phone ||
                !formData.resumeLink ||
                !formData.targetRole
              }
              className="bookslot-btn bookslot-btn-primary"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div>
          <label className="bookslot-label" htmlFor="date">Select Date</label>
          <input
            id="date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            min={new Date().toISOString().split("T")[0]}
            className="bookslot-input"
          />

          <label className="bookslot-label" htmlFor="time">Select Time</label>
          <select
            id="time"
            name="time"
            value={formData.time}
            onChange={handleChange}
            className="bookslot-select"
          >
            <option value="">-- Select Time --</option>
            <option value="10:00 AM">10:00 AM</option>
            <option value="12:00 PM">12:00 PM</option>
            <option value="2:00 PM">2:00 PM</option>
            <option value="4:00 PM">4:00 PM</option>
            <option value="6:00 PM">6:00 PM</option>
          </select>

          <div className="bookslot-btn-group">
            <button
              onClick={handleBack}
              className="bookslot-btn bookslot-btn-secondary"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              disabled={!formData.date || !formData.time}
              className="bookslot-btn bookslot-btn-primary"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 4 && (
        <div>
          <div className="bookslot-summary">
            <p><strong>Service:</strong> {formData.service}</p>
            <p><strong>Date & Time:</strong> {formData.date} at {formData.time}</p>
            <p><strong>Name:</strong> {formData.fullName}</p>
            <p><strong>Email:</strong> {formData.email}</p>
            <p><strong>Phone:</strong> {formData.phone}</p>
          </div>

          <div className="bookslot-checkbox-container">
            <input
              type="checkbox"
              id="tnc"
              name="agree"
              checked={formData.agree}
              onChange={handleChange}
              className="bookslot-checkbox"
            />
            <label htmlFor="tnc">
              I agree to the{" "}
              <a href="/terms" className="bookslot-terms-link" target="_blank" rel="noopener noreferrer">
                Terms & Conditions
              </a>
            </label>
          </div>

          <div className="bookslot-btn-group">
            <button
              onClick={handleBack}
              className="bookslot-btn bookslot-btn-secondary"
            >
              Back
            </button>
            <button
              onClick={handlePayment}
              disabled={!formData.agree}
              className="bookslot-btn bookslot-btn-primary"
            >
              Pay ₹499
            </button>
          </div>
        </div>
      )}

      {(step === 5 || step === 6) && (
        <Step5Confirmation bookingDetails={confirmedBooking} />
      )}

    </div>
  );
};

export default BookSlot;
