import React from "react";
import { CheckCircle } from "lucide-react";

const steps = [
  "Service",
  "Information",
  "Date & Time",
  "Payment",
  "Confirmation",
];

const Stepper = ({ currentStep }) => {
  return (
    <div className="flex justify-between items-center mb-10 px-2">
      {steps.map((label, index) => {
        const stepNum = index + 1;
        const isCompleted = currentStep > stepNum;
        const isActive = currentStep === stepNum;

        return (
          <div key={label} className="flex-1 flex flex-col items-center relative">
            {/* Circle */}
            <div
              className={`w-10 h-10 flex items-center justify-center rounded-full font-bold border-2 z-10 ${
                isCompleted
                  ? "bg-green-500 text-white border-green-500"
                  : isActive
                  ? "bg-red-500 text-white border-red-500"
                  : "bg-gray-300 text-gray-600 border-gray-300"
              }`}
            >
              {isCompleted ? <CheckCircle size={20} className="text-white" /> : stepNum}
            </div>

            {/* Label */}
            <span className="mt-2 text-sm text-center">{label}</span>

            {/* Connector line */}
            {index !== steps.length - 1 && (
              <div className="absolute top-5 left-full w-full h-1 bg-gray-300 z-0">
                <div
                  className={`h-1 ${
                    currentStep > stepNum
                      ? "bg-green-500"
                      : isActive
                      ? "bg-red-500"
                      : "bg-gray-300"
                  }`}
                  style={{ width: "100%" }}
                ></div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Stepper;
