import React, { useState } from "react";

const BookSlot = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    service: "",
    fullName: "",
    email: "",
    phone: "",
    resumeLink: "",
    targetRole: "",
    notes: "",
    date: "",
    time: "",
    acceptedTerms: false,
  });

  const handleNext = () => setStep(prev => prev + 1);
  const handleBack = () => setStep(prev => prev - 1);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 shadow-lg rounded-lg bg-white">
      <div className="mb-6 text-xl font-bold text-center text-red-600">
        Step {step} of 5
      </div>

      {step === 1 && (
        <div>
          <label className="block mb-2 font-medium">Desired Service</label>
          <input
            type="text"
            name="service"
            value={formData.service}
            onChange={handleChange}
            placeholder="e.g., Resume Review, Interview Prep"
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />
          <div className="flex justify-end">
            <button
              onClick={handleNext}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              disabled={!formData.service.trim()}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {/* Placeholder for future steps */}
      {step > 1 && (
        <div className="text-gray-400 text-sm text-center">
          More steps coming soon...
        </div>
      )}
    </div>
  );
};

export default BookSlot;
