import React, { useState } from "react";
import api from '../api';
import Step5Confirmation from "../components/Step5Confirmation";


const BookSlot = () => {
  const [confirmedBooking, setConfirmedBooking] = useState(null);
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    service: "",
    fullName: "",
    email: "",
    phone: "",
    resumeLink: "",
    targetRole: "",
    notes: "",
    date: "",
    time: "",
    acceptedTerms: false,
  });

  const handleNext = () => setStep(prev => prev + 1);
  const handleBack = () => setStep(prev => prev - 1);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handlePayment = async () => {
    try {
      const { data } = await api.post("/payment/create-order", {
        amount: 499,
      });

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID, // replace with your test key
        amount: data.amount,
        currency: "INR",
        name: "Apne Wale Coders",
        description: "Slot Booking Payment",
        order_id: data.id,
        handler: async function (response) {
          try {
            const verify = await api.post("/payment/verify-payment", {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
            });

            const saveRes = await api.post("/booking/confirm", {
              bookingData: formData,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_order_id: response.razorpay_order_id,
              razorpay_signature: response.razorpay_signature,
            });

            setConfirmedBooking(saveRes.data.booking); // <- Save data returned from backend
            setStep(5);

          } catch (err) {
            console.error("Payment verification or save failed:", err);
            alert("Something went wrong");
          }
        },
        prefill: {
          name: formData.fullName,
          email: formData.email,
          contact: formData.phone,
        },
        theme: {
          color: "#EF4444", // red
        },
      };

      const razor = new window.Razorpay(options);
      razor.open();
    } catch (error) {
      console.error("Razorpay error:", error);
      alert("Payment Failed");
    }
  };


  return (
    <div className="max-w-xl mx-auto mt-10 p-6 shadow-lg rounded-lg bg-white">
      <div className="mb-6 text-xl font-bold text-center text-red-600">
        Step {step} of 5
      </div>

      {step === 1 && (
        <div>
          <label className="block mb-2 font-medium">Desired Service</label>
          <input
            type="text"
            name="service"
            value={formData.service}
            onChange={handleChange}
            placeholder="e.g., Resume Review, Interview Prep"
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />
          <div className="flex justify-end">
            <button
              onClick={handleNext}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              disabled={!formData.service.trim()}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {/* Placeholder for future steps */}
      {step === 2 && (
        <div>
          <label className="block mb-2 font-medium">Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <label className="block mb-2 font-medium">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <label className="block mb-2 font-medium">Phone</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <label className="block mb-2 font-medium">Resume Link</label>
          <input
            type="url"
            name="resumeLink"
            value={formData.resumeLink}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <label className="block mb-2 font-medium">Target Role</label>
          <input
            type="text"
            name="targetRole"
            value={formData.targetRole}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <label className="block mb-2 font-medium">Notes (optional)</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          />

          <div className="flex justify-between mt-4">
            <button
              onClick={handleBack}
              className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              disabled={
                !formData.fullName ||
                !formData.email ||
                !formData.phone ||
                !formData.resumeLink ||
                !formData.targetRole
              }
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div>
          <label className="block mb-2 font-medium">Select Date</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
            min={new Date().toISOString().split("T")[0]} // disable past dates
          />

          <label className="block mb-2 font-medium">Select Time</label>
          <select
            name="time"
            value={formData.time}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded mb-4"
          >
            <option value="">-- Select Time --</option>
            <option value="10:00 AM">10:00 AM</option>
            <option value="12:00 PM">12:00 PM</option>
            <option value="2:00 PM">2:00 PM</option>
            <option value="4:00 PM">4:00 PM</option>
            <option value="6:00 PM">6:00 PM</option>
          </select>

          <div className="flex justify-between mt-4">
            <button
              onClick={handleBack}
              className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              disabled={!formData.date || !formData.time}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 4 && (
        <div>
          <h2 className="text-lg font-bold mb-4">Payment Summary</h2>
          
          <div className="bg-gray-100 p-4 rounded mb-4 space-y-2">
            <p><strong>Service:</strong> {formData.service}</p>
            <p><strong>Name:</strong> {formData.fullName}</p>
            <p><strong>Email:</strong> {formData.email}</p>
            <p><strong>Phone:</strong> {formData.phone}</p>
            <p><strong>Resume Link:</strong> {formData.resumeLink}</p>
            <p><strong>Target Role:</strong> {formData.targetRole}</p>
            <p><strong>Date & Time:</strong> {formData.date} at {formData.time}</p>
          </div>

          <div className="mb-4 flex items-center space-x-2">
            <input
              type="checkbox"
              id="tnc"
              checked={formData.agree}
              onChange={(e) =>
                setFormData({ ...formData, agree: e.target.checked })
              }
            />
            <label htmlFor="tnc" className="ml-2">
              I agree to the{' '}
              <button
                type="button"
                onClick={() => alert("Show Terms & Conditions here or redirect")}
                className="text-red-500 underline bg-transparent border-0 p-0 cursor-pointer"
              >
                Terms & Conditions
              </button>
            </label>
          </div>


          <div className="flex justify-between mt-4">
            <button
              onClick={handleBack}
              className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400"
            >
              Back
            </button>
            <button
              onClick={handlePayment}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              disabled={!formData.agree}
            >
              Pay Now
            </button>
          </div>
        </div>
      )}

      {step === 5 && <Step5Confirmation bookingDetails={confirmedBooking} />}
    </div>
  );
};

export default BookSlot;
