import React, { useEffect, useState } from "react";
import api from "../api";

const Dashboard = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const { data } = await api.get("/booking/my-bookings");
        setBookings(data.bookings);
      } catch (error) {
        console.error("Failed to fetch bookings", error);
      }
    };

    fetchBookings();
  }, []);

  return (
    <div className="p-6 min-h-screen bg-gray-50">
      <h2 className="text-3xl font-bold text-red-600 mb-6 text-center">
        My Bookings
      </h2>

      {bookings.length === 0 ? (
        <p className="text-center text-gray-500">You have no bookings yet.</p>
      ) : (
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {bookings.map((booking) => (
            <div
              key={booking._id}
              className="bg-white rounded-2xl shadow-md border p-5 transition hover:shadow-lg"
            >
              <p className="text-lg font-semibold text-red-500 mb-2">
                {booking.service}
              </p>
              <p className="text-gray-700">
                <strong>Date:</strong>{" "}
                {new Date(booking.date).toLocaleDateString()}
              </p>
              <p className="text-gray-700">
                <strong>Time:</strong> {booking.time}
              </p>
              <p className="text-green-600 font-medium mt-2">
                âœ… Confirmed
              </p>
              <p className="text-sm text-gray-500 mt-1">
                Booking ID: <span className="break-all">{booking._id}</span>
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
