import React from "react";
import { useNavigate } from "react-router-dom";
import { CheckCircle } from "lucide-react";

const Step5Confirmation = ({ bookingDetails }) => {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center justify-center min-h-[60vh] text-center px-4">
      <CheckCircle className="text-green-500 w-20 h-20 mb-4 animate-bounce" />
      <h2 className="text-2xl font-bold text-green-600">Payment Successful!</h2>
      <p className="text-gray-700 mt-2">Your slot has been booked successfully. ðŸŽ‰</p>

      <div className="mt-6 w-full max-w-md bg-white shadow-md rounded-lg p-6 text-left">
        <h3 className="text-lg font-semibold mb-2 text-red-500">Booking Details</h3>
        <p><strong>Service:</strong> {bookingDetails?.service}</p>
        <p><strong>Name:</strong> {bookingDetails?.fullName}</p>
        <p><strong>Email:</strong> {bookingDetails?.email}</p>
        <p><strong>Phone:</strong> {bookingDetails?.phone}</p>
        <p><strong>Date & Time:</strong> {bookingDetails?.date} at {bookingDetails?.time}</p>
        <p><strong>Booking ID:</strong> {bookingDetails?._id}</p>
      </div>

      <button
        onClick={() => navigate("/dashboard")}
        className="mt-6 bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-full"
      >
        Go to Dashboard
      </button>
    </div>
  );
};

export default Step5Confirmation;
