import React, { useState } from "react";
import api from '../api';
import Step5Confirmation from "../components/Step5Confirmation";
import Stepper from "../components/Stepper";

const BookSlot = () => {
  const [confirmedBooking, setConfirmedBooking] = useState(null);
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    service: "",
    fullName: "",
    email: "",
    phone: "",
    resumeLink: "",
    targetRole: "",
    notes: "",
    date: "",
    time: "",
    agree: false,
  });

  const handleNext = () => setStep(prev => prev + 1);
  const handleBack = () => setStep(prev => prev - 1);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handlePayment = async () => {
    try {
      const { data } = await api.post("/payment/create-order", {
        amount: 499,
      });

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: data.amount,
        currency: "INR",
        name: "Apne Wale Coders",
        description: "Slot Booking Payment",
        order_id: data.id,
        handler: async function (response) {
          try {
            await api.post("/payment/verify-payment", {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
            });

            const saveRes = await api.post("/booking/confirm", {
              bookingData: formData,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_order_id: response.razorpay_order_id,
              razorpay_signature: response.razorpay_signature,
            });

            setConfirmedBooking(saveRes.data.booking);
            setStep(5);

          } catch (err) {
            console.error("Payment verification or save failed:", err);
            alert("Something went wrong during payment confirmation.");
          }
        },
        prefill: {
          name: formData.fullName,
          email: formData.email,
          contact: formData.phone,
        },
        theme: {
          color: "#EF4444",
        },
      };

      const razor = new window.Razorpay(options);
      razor.open();
    } catch (error) {
      console.error("Razorpay error:", error);
      alert("Payment Failed");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-12 p-8 bg-white rounded-lg shadow-lg border border-gray-200">
      <Stepper currentStep={step} />

      {step === 1 && (
        <div>
          <label className="block mb-2 font-semibold text-gray-700">Desired Service</label>
          <input
            type="text"
            name="service"
            value={formData.service}
            onChange={handleChange}
            placeholder="e.g., Resume Review, Interview Prep"
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-6"
          />
          <div className="flex justify-end">
            <button
              onClick={handleNext}
              disabled={!formData.service.trim()}
              className={`px-6 py-2 rounded font-semibold text-white transition-colors ${
                formData.service.trim()
                  ? "bg-red-600 hover:bg-red-700"
                  : "bg-red-300 cursor-not-allowed"
              }`}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div>
          <label className="block mb-2 font-semibold text-gray-700">Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Phone</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Resume Link</label>
          <input
            type="url"
            name="resumeLink"
            value={formData.resumeLink}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Target Role</label>
          <input
            type="text"
            name="targetRole"
            value={formData.targetRole}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Notes (optional)</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-6 resize-y"
            rows={4}
            placeholder="Additional info or questions..."
          />

          <div className="flex justify-between">
            <button
              onClick={handleBack}
              className="px-6 py-2 rounded bg-gray-300 font-semibold text-gray-700 hover:bg-gray-400 transition-colors"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              disabled={
                !formData.fullName ||
                !formData.email ||
                !formData.phone ||
                !formData.resumeLink ||
                !formData.targetRole
              }
              className={`px-6 py-2 rounded font-semibold text-white transition-colors ${
                formData.fullName &&
                formData.email &&
                formData.phone &&
                formData.resumeLink &&
                formData.targetRole
                  ? "bg-red-600 hover:bg-red-700"
                  : "bg-red-300 cursor-not-allowed"
              }`}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div>
          <label className="block mb-2 font-semibold text-gray-700">Select Date</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            min={new Date().toISOString().split("T")[0]}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-4"
          />

          <label className="block mb-2 font-semibold text-gray-700">Select Time</label>
          <select
            name="time"
            value={formData.time}
            onChange={handleChange}
            className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-red-400 mb-6"
          >
            <option value="">-- Select Time --</option>
            <option value="10:00 AM">10:00 AM</option>
            <option value="12:00 PM">12:00 PM</option>
            <option value="2:00 PM">2:00 PM</option>
            <option value="4:00 PM">4:00 PM</option>
            <option value="6:00 PM">6:00 PM</option>
          </select>

          <div className="flex justify-between">
            <button
              onClick={handleBack}
              className="px-6 py-2 rounded bg-gray-300 font-semibold text-gray-700 hover:bg-gray-400 transition-colors"
            >
              Back
            </button>
            <button
              onClick={handleNext}
              disabled={!formData.date || !formData.time}
              className={`px-6 py-2 rounded font-semibold text-white transition-colors ${
                formData.date && formData.time
                  ? "bg-red-600 hover:bg-red-700"
                  : "bg-red-300 cursor-not-allowed"
              }`}
            >
              Next
            </button>
          </div>
        </div>
      )}

      {step === 4 && (
        <div>
          <h2 className="text-xl font-bold mb-6 text-gray-800">Payment Summary</h2>

          <div className="bg-gray-50 p-6 rounded shadow-inner mb-6 space-y-3 text-gray-700">
            <p><strong>Service:</strong> {formData.service}</p>
            <p><strong>Name:</strong> {formData.fullName}</p>
            <p><strong>Email:</strong> {formData.email}</p>
            <p><strong>Phone:</strong> {formData.phone}</p>
            <p><strong>Resume Link:</strong> {formData.resumeLink}</p>
            <p><strong>Target Role:</strong> {formData.targetRole}</p>
            <p><strong>Date & Time:</strong> {formData.date} at {formData.time}</p>
          </div>

          <div className="mb-6 flex items-center space-x-3">
            <input
              type="checkbox"
              id="tnc"
              name="agree"
              checked={formData.agree}
              onChange={handleChange}
              className="w-5 h-5 text-red-600 border-gray-300 rounded focus:ring-red-500"
            />
            <label htmlFor="tnc" className="text-gray-700 text-sm select-none">
              I agree to the <a href="/terms" className="text-red-600 underline hover:text-red-700">Terms & Conditions</a>
            </label>
          </div>

          <div className="flex justify-between">
            <button
              onClick={handleBack}
              className="px-6 py-2 rounded bg-gray-300 font-semibold text-gray-700 hover:bg-gray-400 transition-colors"
            >
              Back
            </button>
            <button
              onClick={handlePayment}
              disabled={!formData.agree}
              className={`px-6 py-2 rounded font-semibold text-white transition-colors ${
                formData.agree ? "bg-red-600 hover:bg-red-700" : "bg-red-300 cursor-not-allowed"
              }`}
            >
              Pay ₹499
            </button>
          </div>
        </div>
      )}

      {step === 5 && (
        <Step5Confirmation bookingDetails={confirmedBooking} />
      )}
    </div>
  );
};

export default BookSlot;
